mapper.update:
update notice 

	
		notice_title=#{noticeTitle}, notice_content=#{noticeContent}, noticeImage=#{noticeImage}
		, notice_update=sysdate 
	
	
		notice_title=#{noticeTitle}, notice_content=#{noticeContent}, notice_update=sysdate  
	

 where notice_no=#{noticeNo}

mapper.select:
select * from (select rownum rn, notice.* from (select notice_no, notice_title
	, notice_content, notice_image, notice_date, notice_update, notice_count, notice_user_no
	from notice order by notice_no desc) notice) where rn between #{startRow} and #{endRow}
